import React from 'react'

function InfoToolTip({text, isOpen, onClose}) {
    return (
        // <div className={`popup popup_type_infoTooltip ${isOpen ? 'popup_active' : ''}`}>
        <div className="popup popup_type_infoTooltip popup_active">
            <div className="popup__container">
            <h2 className="popup__title">{props.title}</h2>
                <form
                    name={props.name}
                    className={`form form_type_${props.name}`}
                    onSubmit={props.onSubmit}
                    noValidate
                >
                    {props.children}
                    <button type="submit" className="form__save-button">{props.saveButton}</button>
                </form>

                <button onClick={onClose}
                    aria-label="Закрыть окно просмотра" type="button"
                    className="popup__close-button" />
            </div>
        </div>
    )
}

export default InfoToolTip
