import React from 'react'

import successIcon from '../images/success.jpeg'
import notSuccessIcon from '../images/not-success.jpeg'

function InfoToolTip({isOpen, onClose, authMessage}) {
    return (
        <div className={`popup popup_type_infoTooltip ${isOpen ? 'popup_active' : ''}`}>
            <div className="popup__container">
                <img
                    className="popup_type_info__successIcon"
                    src={authMessage}
                    alt={authMessage.text}
                />
                <h2 className="popup__title popup___title_type_info">
                    {authMessage.text}
                </h2>

                <button onClick={onClose}
                    aria-label="Закрыть окно просмотра" type="button"
                    className="popup__close-button" />
            </div>
        </div>
    )
}

export default InfoToolTip
