import React from 'react'

function InfoToolTip(props) {
    return (
        // <div className={`popup popup_type_infoTooltip ${isOpen ? 'popup_active' : ''}`}>
        <div className="popup popup_type_infoTooltip popup_active">
            <div className="popup__container">

                <h2 className="popup__title">
                    {props.isSuccess ?
                        'Вы успешно зарегистрировались!'
                        :
                        'Что-то пошло не так! Пропробуйте ещё раз.'
                    }
                </h2>

                <button onClick={props.onClose}
                    aria-label="Закрыть окно просмотра" type="button"
                    className="popup__close-button" />
            </div>
        </div>
    )
}

export default InfoToolTip
